/* 
    主路由组件
*/

import React from "react";
import { Switch, Route, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import Cookies from "js-cookie"; //可以操作前端cookie的对象 set()/remove()

import LaobanInfo from "../laoban-info/laoban-info";
import DashenInfo from "../dashen-info/dashen-info";
import { getRedirectTo } from "../../utils";
import { getUser } from "../../redux/actions";

class Main extends React.Component {
  componentDidMount() {
    /* 实现异步过程 */

    // 登陆过，但是关掉了该窗口(cookies中有userid)，
    // 之后再打开没有再次登录(redux管理的user中没有_id)
    const userid = Cookies.get("userid");
    const { _id } = this.props.user;
    if (userid && !_id) {
      // 发送异步请求，获取user信息
      // 需要在后台先写请求响应，然后在前台写发送ajax请求的函数、redux等，最后在可以在这里使用
      this.props.getUser();
    }
  }

  render() {
    // 读取cookies中的userid
    const userid = Cookies.get("userid");
    if (!userid) {
      // 如果没有，则自动重定向到登录界面
      return <Redirect to="/login"></Redirect>;
    }

    // 判断redux管理的user中是否有_id
    const { user } = this.props;
    // debugger;
    if (!user._id) {
      // 如果user没有_id,则返回一个null,不做任何显示
      return null;
    } else {
      /* 如果有_id,则显示对应的界面，比如大神界面，老板界面，个人中心界面等 */

      // 获取请求的路径
      let path = this.props.location.pathname;
      // 如果请求的是根路径，就需要根据user的header和type计算得到一个重定向的路径，并且自动重定向
      if (path === "/") {
        path = getRedirectTo(user.type, user.header);
        return <Redirect to={path}></Redirect>;
      }
    }

    // 根据user的header和type计算得到一个重定向的路径，并且自动重定向
    return (
      <div>
        <Switch>
          <Route path="/laobaninfo" component={LaobanInfo}></Route>
          <Route path="/dasheninfo" component={DashenInfo}></Route>
        </Switch>
      </div>
    );
  }
}

export default connect((state) => ({ user: state.user }), { getUser })(Main);

/* 
1 实现自动登陆：
    1) 登陆过，但是关掉了该窗口(cookies中有userid)，之后再打开没有再次登录(redux管理的user中没有_id)，
       发送请求获取对应的用户信息   需要在componentDidMount()中实现
    2) cookies中没有userid(之前没有登录，或者登陆了也注销了) 自动进入login界面
    3) 判断redux管理的user中是否有_id,没有暂时不做任何显示
    4) 有说明当前已经登陆，则显示对应的界面
    5) 如果已经登陆了：如果请求根路径，会根据user的header和type计算得到一个重定向的路径，
        并且自动重定向
*/
