import React from "react";
import { connect } from "react-redux";
import { NavBar, List, InputItem } from "antd-mobile";

const Item = List.Item;

class Chat extends React.Component {
  handleSend = () => {
    const from = this.props.user._id;
    // const to =
  };
  render() {
    return (
      <div id="chat-page">
        <NavBar>aa</NavBar>
        <List>
          <Item thumb={require(`../../assets/images/头像1.png`)}>你好</Item>
          <Item thumb={require(`../../assets/images/头像1.png`)}>你好 2</Item>
          <Item extra="我" className="chat-me">
            很好
          </Item>
          <Item extra="我" className="chat-me">
            很好 2
          </Item>
        </List>
        <div>
          <InputItem
            placeholder="请输入"
            extra={<span onClick={this.handleSend}>发送</span>}
          ></InputItem>
        </div>
      </div>
    );
  }
}

export default connect((state) => ({ user: state.user }), {})(Chat);
