/* 
    主路由组件
*/

import React from "react";
import { Switch, Route, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import Cookies from "js-cookie"; //可以操作前端cookie的对象 set()/remove()

import LaobanInfo from "../laoban-info/laoban-info";
import DashenInfo from "../dashen-info/dashen-info";
import { getRedirectTo } from "../../utils";

class Main extends React.Component {
  componentDidMount() {
    // 登陆过，但是关掉了该窗口(cookies中有userid)，
    // 之后再打开没有再次登录(redux管理的user中没有_id)
  }

  render() {
    // 读取cookies中的userid
    const userid = Cookies.get("userid");
    if (!userid) {
      // 如果没有，则自动重定向到登录界面
      return <Redirect to="/login"></Redirect>;
    }
    // 如果有，读取redux中的user状态
    const { user } = this.props;
    if (!user._id) {
      // 如果user没有_id,则返回一个null,不做任何显示
      return null;
    } else {
      // 如果有_id,则显示对应的界面，比如大神界面，老板界面，个人中心界面等
      // 如果请求的是根路径，就需要根据user的header和type计算得到一个重定向的路径，并且自动重定向

      // 获取请求的路径
      let path = this.props.location.pathname;
      if (path === "/") {
        path = getRedirectTo(user.type, user.header);
        return <Redirect to={path}></Redirect>;
      }
    }

    // 根据user的header和type计算得到一个重定向的路径，并且自动重定向
    return (
      <div>
        <Switch>
          <Route path="/laobaninfo" component={LaobanInfo}></Route>
          <Route path="/dasheninfo" component={DashenInfo}></Route>
        </Switch>
      </div>
    );
  }
}

export default connect((state) => ({ user: state.user }), {})(Main);

/* 
1 实现自动登陆：
    情况1： 登陆过，但是关掉了该窗口(cookies中有userid)，之后再打开没有再次登录(redux管理的user中没有_id)，
        发送请求获取对应的用户信息，暂时不做任何显示
    情况2： cookies中没有userid
        自动进入login界面
2 如果已经登陆了：
    如果请求根路径，会根据user的header和type计算得到一个重定向的路径，并且自动重定向
*/
