import React from "react";
import { connect } from "react-redux";
import { NavBar, List, InputItem } from "antd-mobile";
import { sendMsg } from "../../redux/actions";

const Item = List.Item;

class Chat extends React.Component {
  state = {
    content: "",
  };
  handleSend = () => {
    // 收集数据
    const from = this.props.user._id;
    const to = this.props.match.params.userid;
    const content = this.state.content.trim();
    // 发送请求(发消息)
    if (content) {
      // 异步操作
      this.props.sendMsg({ from, to, content });
    }
    // 清除输入数据
    this.setState({ content: "" });
  };
  render() {
    const { user, chat } = this.props;
    const { users, chatMsgs } = chat;
    //当前chatMsgs中包含我和所有其他用户的聊天信息，我现在只需要显示我和某个特定用户的聊天信息
    // 所以需要过滤
    // 先得到当前的chat_id
    const meId = user._id;
    const targetId = this.props.match.params.userid;
    const chat_id = [meId, targetId].sort().join("_");
    const msgs = chatMsgs.filter((msg) => msg.chat_id === chat_id);
    // msgs中的消息有两种情况：我发的，对方发的

    // 获取目标用户的头像
    const targetHeader = users[targetId].header;
    const targetIcon = require(`../../assets/images/${targetHeader}.png`);
    // 有一个问题，对方可能还没有完善信息，并不具备icon

    return (
      <div id="chat-page">
        <NavBar>aa</NavBar>
        <List>
          {msgs.map((msg) => {
            if (msg.to === meId) {
              // 对方发给我的
              // 获取对方的头像
              const header = users.return(
                <Item
                  key={msg._id}
                  thumb={`../../assets/images/${targetIcon}.png`}
                >
                  msg.content
                </Item>,
                {
                  /* 没有必要在map中获取头像，因为目标用户的头像是确定的，所以直接在外层获取一次就可以了
                  <Item key={msg._id} thumb={require(`../../assets/images/${header}.png`)}>msg.content</Item> */
                }
              );
            } else {
              //我发给对方的
              return (
                <Item key={msg._id} extra="我" className="chat-me">
                  {msg.content}
                </Item>
              );
            }
          })}
        </List>
        <div className="am-tab-bar">
          <InputItem
            placeholder="请输入"
            extra={<span onClick={this.handleSend}>发送</span>}
            value={this.state.content}
            onChange={(val) => this.setState({ content: val })}
          ></InputItem>
        </div>
      </div>
    );
  }
}

export default connect((state) => ({ user: state.user, chat: state.chat }), {
  sendMsg,
})(Chat);
